package org.modelix.model.mpsplugin.plugin

import com.intellij.openapi.project.Project
import com.intellij.openapi.wm.ToolWindow
import com.intellij.openapi.wm.ToolWindowFactory
import com.intellij.ui.content.ContentFactory
import org.modelix.model.mpsplugin.history.CloudView

/*Generated by MPS */
class CloudToolTool : ToolWindowFactory {
    private var component: CloudView? = null
    override fun createToolWindowContent(project: Project, toolWindow: ToolWindow) {
        component = CloudView()
        toolWindow.contentManager.addContent(
            ContentFactory.SERVICE.getInstance().createContent(component, "Modelix Server", false),
        )
    }

    companion object {
        private val ICON = IconContainer.ICON_a0
    }
}
